    this.state = {
      users : [
        {
          id: 1,
          firstname: 'Bob',
          lastname: 'Marley',
          bio: 'Lorem asdfasdfadsf',
          age: 40,
          isHappy: true
        },
        {
          id: 2,
          firstname: 'John',
          lastname: 'Doe',
          bio: 'Lorem asdfasdfadsf',
          age: 22,
          isHappy: false
        }
      ]
    }
  }
  render() {
    return (<div className="name">
      <Header titel="Список пользователей" />
      <main>
        <Users users={this.state.users} />
      </main>
      <aside>
        <AddUser />
      </aside>
    </div>)
  }

  addUser

}

export default App
