import React from "react"
class Users extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      users: [
        {
          id: 1,
          firstname: 'Bob',
          lastname: 'Marley',
          bio: 'Lorem asdfasdfadsf',
          age: 40,
          isHappy: true
        },
        {
          id: 2,
          firstname: 'John',
          lastname: 'Doe',
          bio: 'Lorem asdfasdfadsf',
          age: 22,
          isHappy: false
        },
      ]
    }
  }
  render() {
    if (this.state.users.length > 0) {
      return (<div>
        {this.state.users.map((element) => (<div className="user" key={element.id}>
          <h3>{element.firstname} {element.lastname}</h3>
          <p>{element.bio}</p>
          <b>{element.isHappy ? "Счастлив :)" : "Не особо :("}</b>
        </div>))}
      </div>
      )
    }
    else {
      return (<div className="user">
        <h3>Пользователей нет</h3>
      </div>)
    }
  }


}

export default Users
