import React from "react"
class AddUser extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      firstname: "",
      lastname: "",
      bio: "",
      age: 1,
      isHappy: false
    }
  }
  render() {
    return (
      <form>
        <input placeholder="Имя" onChenge={(e) => this.setState({ firstname: e.target.value })} />
        <input placeholder="Фамилия" onChenge={(e) => this.setState({ lastname: e.target.value })} />
        <textarea placeholder="Биография" onChenge={(e) => this.setState({ bio: e.target.value })} />
        <input placeholder="Возвраст" onChenge={(e) => this.setState({ age: e.target.value })} />
        <label htmlFor="isHappy">Счастлив?</label>
        <input type="checkbox" id="isHappy" onChenge={(e) => this.setState({ isHappy: e.target.checked })} />
        <button type="button" onClick={() => this.props.onAdd({
          firstname: this.state.firstname,
          lastname: this.state.lastname,
          bio: this.state.bio,
          age: this.state.age,
        }
        )}>Добавить</button>
      </form>
    )
  }


}

export default AddUser
