import React from "react"
import Header from "./components/Header"
import background from "./image/i.png"

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      helpText: "hi hi hi hi hi",
      userData: ""
    }
    this.inputClick = this.inputClick.bind(this)
  }
  componentDidUpdate(prevProp) {
    if(this.state.helpText !== "Help")
      log
  }
  helpText = "hi hi hi hi hi"
  render() {
    return (<div className="name">
      <Header titel="Шапка сайта" />
      <Header titel="Опа" />
      <Header titel="Дэздемона" />
      <img src={background}></img>
      <h1>Привет</h1>
      <h2>{this.state.userData}</h2>
      <input placeholder="text" onClick={this.inputClick}
        onChange={event => this.setState({ userData: event.target.value })} />
      <p>{this.state.helpText}</p>
    </div>)
  }

  inputClick() {
    this.setState({ helpText: "Change" })
    console.log("Click")
  }
  inputMouseOver() { console.log("Mouse Over") }
}

export default App
